"use client";
import { animate, motion } from "framer-motion";
import React from "react";
import { cn } from "@/lib/utils";
import { GoCopilot } from "react-icons/go";
import { GlowingEffect } from "@/components/ui/glowing-effect";
import { useLanguage } from "@/contexts/LanguageContext";
import { AnimatedText } from "@/components/ui/AnimatedText";

export function ToolsShowcase() {
  const { t } = useLanguage();

  return (
    <Card>
      <CardSkeletonContainer>
        <Skeleton />
      </CardSkeletonContainer>
      <CardTitle>
        <AnimatedText>{t("tools.title")}</AnimatedText>
      </CardTitle>
      <CardDescription>
        <AnimatedText block>{t("tools.description")}</AnimatedText>
      </CardDescription>
    </Card>
  );
}

const Skeleton = () => {
  return (
    <div className="p-8 overflow-hidden h-full relative flex items-center justify-center bg-neutral-100 dark:bg-neutral-900">
      <div className="absolute inset-0 z-0">
         <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-transparent via-cyan-500/10 to-transparent opacity-20 blur-3xl transform -skew-x-12" />
      </div>
      
      <div className="flex flex-col items-center justify-center z-10 w-full">
        <div className="relative w-full overflow-hidden mask-image-linear-gradient">
           <div className="flex overflow-hidden">
             <motion.div
               initial={{ x: 0 }}
               animate={{ x: "-50%" }}
               transition={{
                 duration: 20,
                 ease: "linear",
                 repeat: Infinity,
               }}
               className="flex flex-row gap-12 items-center pr-12"
             >
               {[...new Array(4)].map((_, i) => (
                 <React.Fragment key={i}>
                   <ToolIconItem><OpenAILogo className="h-10 w-10 dark:text-white" /></ToolIconItem>
                   <ToolIconItem><GeminiLogo className="h-10 w-10" /></ToolIconItem>
                   <ToolIconItem><GoCopilot className="h-10 w-10 dark:text-white" /></ToolIconItem>
                   <ToolIconItem><ClaudeLogo className="h-10 w-10" /></ToolIconItem>
                 </React.Fragment>
               ))}
             </motion.div>
           </div>
        </div>
      </div>

      <div className="h-40 w-px absolute top-20 m-auto z-40 bg-gradient-to-b from-transparent via-cyan-500 to-transparent animate-move">
        <div className="w-10 h-32 top-1/2 -translate-y-1/2 absolute -left-10">
          <Sparkles />
        </div>
      </div>
    </div>
  );
};

const ToolIconItem = ({ children }: { children: React.ReactNode }) => {
  return (
    <div className="flex items-center justify-center h-16 w-16 rounded-2xl bg-white/50 dark:bg-neutral-800/50 backdrop-blur-sm border border-neutral-200 dark:border-neutral-700 shadow-sm hover:shadow-md transition-all duration-300 hover:scale-110">
      {children}
    </div>
  );
};



const Sparkles = () => {
  const randomMove = () => Math.random() * 2 - 1;
  const randomOpacity = () => Math.random();
  const random = () => Math.random();
  return (
    <div className="absolute inset-0">
      {[...new Array(12)].map((_, i) => (
        <motion.span
          key={`star-${i}`}
          animate={{
            top: `calc(${random() * 100}% + ${randomMove()}px)`,
            left: `calc(${random() * 100}% + ${randomMove()}px)`,
            opacity: randomOpacity(),
            scale: [1, 1.2, 0],
          }}
          transition={{
            duration: random() * 2 + 4,
            repeat: Infinity,
            ease: "linear",
          }}
          style={{
            position: "absolute",
            top: `${random() * 100}%`,
            left: `${random() * 100}%`,
            width: `2px`,
            height: `2px`,
            borderRadius: "50%",
            zIndex: 1,
          }}
          className="inline-block bg-black dark:bg-white"
        ></motion.span>
      ))}
    </div>
  );
};

export const Card = ({
  className,
  children,
}: {
  className?: string;
  children: React.ReactNode;
}) => {
  return (
    <div className={cn("relative rounded-xl w-full max-w-sm mx-auto", className)}>
      <GlowingEffect
        spread={60}
        glow={true}
        disabled={false}
        proximity={64}
        inactiveZone={0.01}
        borderWidth={2}
      />
      <div
        className={cn(
          "h-full w-full p-4 md:p-6 lg:p-8 rounded-xl border border-[rgba(255,255,255,0.10)] dark:bg-[rgba(40,40,40,0.70)] bg-gray-100 shadow-[2px_4px_16px_0px_rgba(248,248,248,0.06)_inset] group relative z-10",
        )}
      >
        {children}
      </div>
    </div>
  );
};

export const CardTitle = ({
  children,
  className,
}: {
  children: React.ReactNode;
  className?: string;
}) => {
  return (
    <h3
      className={cn(
        "text-lg font-semibold text-gray-800 dark:text-white py-2",
        className
      )}
    >
      {children}
    </h3>
  );
};

export const CardDescription = ({
  children,
  className,
}: {
  children: React.ReactNode;
  className?: string;
}) => {
  return (
    <p
      className={cn(
        "text-sm font-normal text-neutral-600 dark:text-neutral-400 max-w-sm",
        className
      )}
    >
      {children}
    </p>
  );
};

export const CardSkeletonContainer = ({
  className,
  children,
  showGradient = true,
}: {
  className?: string;
  children: React.ReactNode;
  showGradient?: boolean;
}) => {
  return (
    <div
      className={cn(
        "h-[12rem] md:h-[15rem] lg:h-[20rem] rounded-xl z-40",
        className,
        showGradient &&
          "bg-neutral-300 dark:bg-[rgba(40,40,40,0.70)] [mask-image:radial-gradient(50%_50%_at_50%_50%,white_0%,transparent_100%)]"
      )}
    >
      {children}
    </div>
  );
};

